INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86149, 'Didanosine', 'Cinoxacin', 'Moderate', 'INTERVAL: The concomitant administration with didanosine buffered tablets or pediatric oral solution may reduce the oral bioavailability of cinoxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine. A similar interaction is expected with other quinolones that also form chelates with divalent and trivalent metallic cations.', NULL, 'Cinoxacin should be administered at least 2 hours before or 2 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', NULL, 'Cinoxacin should be administered at least 2 hours before or 2 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86250/', '[1] Sahai J "Avoiding the ciprofloxacin-didanosine interaction." Ann Intern Med 123 (1995): 394-5[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[4] Knupp CA, Barbhaiya RH "A multiple-dose pharmacokinetic interaction study between didanosine (videx(r)) and ciprofloxacin (cipro(r)) in male subjects seropositive for HIV but asymptomatic." Biopharm Drug Dispos 18 (1997): 65-77[5] "Product Information. Cinobac (cinoxacin)." Oclassen Pharmaceuticals Inc, San Rafael, CA.[6] Sahai J, Gallicano K, Oliveras L, Khaliq S, Hawley-Foss N, Garber G "Cations in the didanosine tablet reduce ciprofloxacin bioavailability." Clin Pharmacol Ther 53 (1993): 292-7[7] Polk RE "Drug-drug interactions with ciprofloxacin and other fluoroquinolones." Am J Med 87 (1989): s76-81[8] Damle BD, Mummaneni V, Kaul S, Knupp C "Lack of Effect of Simultaneously Administered Didanosine Encapsulated Enteric Bead Formulation (Videx EC) on Oral Absorption of Indinavir, Ketoconazole, or Ciprofloxacin." Antimicrob Agents Chemother 46 (2002): 385-91[9] Marchbanks CR "Drug-drug interactions with fluoroquinolones." Pharmacotherapy 13 (1993): s23-8[10] Deppermann KM, Lode H "Fluoroquinolones: interaction profile during enteral absorption." Drugs 45 Suppl 3 (1993): 65-72[11] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86150, 'Didanosine', 'Dapsone', 'Moderate', 'Limited data suggest that didanosine may reduce the therapeutic efficacy of dapsone in the prophylaxis of Pneumocystis jiroveci pneumonia, formerly known as Pneumocystis carinii pneumonia (PCP). The risk of peripheral neuropathy may be increased during concomitant use of didanosine and dapsone. These agents are individually neurotoxic and may have additive effects during coadministration. However, clinical data are lacking.', NULL, 'Clinicians should be aware of the potential for diminished effects of dapsone during coadministration with didanosine, although the interaction is not well established.', NULL, 'Clinicians should be aware of the potential for diminished effects of dapsone during coadministration with didanosine, although the interaction is not well established. Some clinicians have suggested taking dapsone at least 2 hours before or 6 hours after didanosine, but more recent data suggest that this may not be necessary. Patients should be advised of the possibility for increased risk of neurotoxicity, and monitored closely for symptoms of neuropathy such as visual disturbances, loss of fine motor function, and burning, tingling, pain, or numbness in the hands and feet. Consideration should be given to immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage. If necessary, therapy should generally be reinstituted only after resolution of neuropathy symptoms or return of symptoms to baseline status. In some cases, reduced dosages may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com