INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86252, 'Didanosine', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86353/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86253, 'Didanosine', 'Ribavirin', 'Major', 'Coadministration with ribavirin, a nucleoside analog, may increase exposure to didanosine (ddI) or its active triphosphate metabolite. Both the antiretroviral effect and clinical toxicities of ddI may be enhanced. The mechanism is inhibition of inosine 5''-monophosphate (IMP) dehydrogenase by an initial metabolite of ribavirin, resulting in decreased synthesis of guanine nucleotides that would otherwise compete with the active ddI metabolite for HIV-1 reverse transcriptase. In addition, ddI phosphorylation to the active metabolite may be enhanced by the accumulation of IMP, which is an efficient phosphate donor. Pharmacodynamically, the risk of mitochondrial toxicities such as lipodystrophy, peripheral neuropathy, pancreatitis, and lactic acidosis associated with ddI and other nucleoside reverse transcriptase inhibitors may also be increased during coadministration with ribavirin. Nucleoside analogs alone and in combination have been associated with these and other toxicities.', NULL, 'The concomitant use of oral ribavirin and didanosine is considered contraindicated by US authorities.', NULL, 'The concomitant use of oral ribavirin and didanosine is considered contraindicated by US authorities.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86354/', '[1] "Product Information. Copegus (ribavirin)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Rebetron (interferon-alfa-2b-ribavirin)." Schering Laboratories, Kenilworth, NJ.[3] Balzarini J, Lee CK, Herdewijn P, De Clercq E "Mechanism of the potential effect of ribavirin on the activity of 2'',3''-dideoxyinosine against human immunodeficiency virus." J Biol Chem 266 (1991): 21509-14[4] Harvie P, Omar RF, Dusserre N, et al "Ribavirin potentiates the efficacy and toxicity of 2'',3''-dideoxyinosine in the murine acquired immunodeficiency syndrome model." J Pharmacol Exp Ther 279 (1966): 1009-17[5] Guyader D, Poinsignon Y, Cano Y, Saout L "Fatal lactic acidosis in a HIV-positive patient treated with interferon and ribavirin for chronic hepatitis C." J Hepatol 37 (2002): 289-91[6] Japour AJ, Lertora JJ, Meehan PM, et al "A phase-I study of the safety, pharmacokinetics, and antiviral activity of combination didanosine and ribavirin in patients with HIV-1 disease. AIDS Clinical Trials Group 231 Protocol Team." J Acquir Immune Defic Syndr Hum Retrovirol 13 (1996): 235-46[7] Kakuda TN, Brinkman K "Mitochondrial toxic effects and ribavirin." Lancet 357 (2001): 1802-3[8] Lafeuillade A, Hittinger G, Chadapaud S "Increased mitochondrial toxicity with ribavirin in HIV/HCV coinfection." Lancet 357 (2001): 280-1[9] Salmon-Ceron D, Chauvelot-Moachon L, Abad S, Silberman B, Sogni P "Mitochondrial toxic effects and ribavirin." Lancet 357 (2001): 1803-4[10] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[11] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[12] "Product Information. Copegus (ribavirin)." Roche Laboratories, Nutley, NJ.[13] "Product Information. Rebetol (ribavirin)." Schering-Plough Corporation, Kenilworth, NJ.', 1767365205);
