INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86261, 'Didanosine', 'Salicylic acid (sodium)', 'Moderate', 'Chronic administration of antacids may reduce serum salicylate concentrations in patients receiving large doses of aspirin or other salicylates. The mechanism involves reduction in salicylate renal tubular reabsorption due to urinary alkalinization by antacids, resulting in increased renal salicylate clearance.', NULL, 'Patients treated chronically with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) and large doses of salicylates (i.', NULL, 'Patients treated chronically with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) and large doses of salicylates (i.e. 3 g/day or more) should be monitored for potentially diminished or inadequate analgesic and anti-inflammatory effects, and the salicylate dosage adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86362/', '[1] Gaspari F, Vigano G, Locatelli M, Remuzzi G "Influence of antacid administrations on aspirin absorption in patients with chronic renal failure on maintenance hemodialysis." Am J Kidney Dis 11 (1988): 338-42[2] Covington TR, Lawson LC, Young LL, eds. "Handbook of Nonprescription Drugs. 10th ed." Washington, DC: American Pharmaceutical Association (1993):[3] Levy G, Lampman T, Kamath BL, Garrettson LK "Decreased serum salicylate concentrations in children with rheumatic fever treated with antacid." N Engl J Med 293 (1975): 323-5[4] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85[5] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[6] Miners JO "Drug interactions involving aspirin (acetylsalicylic acid) and salicylic acid." Clin Pharmacokinet 17 (1989): 327-44[7] Furst DE "Clinically important interactions of nonsteroidal antiinflammatory drugs with other medications." J Rheumatol Suppl 17 (1988): 58-62[8] Shastri RA "Effect of antacids on salicylate kinetics." Int J Clin Pharmacol Ther Toxicol 23 (1985): 480-4', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86262, 'Didanosine', 'Sparfloxacin', 'Moderate', 'INTERVAL: Concomitant administration with didanosine buffered tablets or pediatric oral solution may reduce the oral bioavailability of sparfloxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine.', NULL, 'Sparfloxacin should be administered at least 4 hours before didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', NULL, 'Sparfloxacin should be administered at least 4 hours before didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86363/', '[1] Polk RE "Drug-drug interactions with ciprofloxacin and other fluoroquinolones." Am J Med 87 (1989): s76-81[2] Sahai J, Gallicano K, Oliveras L, Khaliq S, Hawley-Foss N, Garber G "Cations in the didanosine tablet reduce ciprofloxacin bioavailability." Clin Pharmacol Ther 53 (1993): 292-7[3] Damle BD, Mummaneni V, Kaul S, Knupp C "Lack of Effect of Simultaneously Administered Didanosine Encapsulated Enteric Bead Formulation (Videx EC) on Oral Absorption of Indinavir, Ketoconazole, or Ciprofloxacin." Antimicrob Agents Chemother 46 (2002): 385-91[4] Johnson RD, Dorr MB, Talbot GH, Caille G "Effect of Maalox on the oral absorption of sparfloxacin." Clin Ther 20 (1998): 1149-58[5] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[6] Knupp CA, Barbhaiya RH "A multiple-dose pharmacokinetic interaction study between didanosine (videx(r)) and ciprofloxacin (cipro(r)) in male subjects seropositive for HIV but asymptomatic." Biopharm Drug Dispos 18 (1997): 65-77[7] Marchbanks CR "Drug-drug interactions with fluoroquinolones." Pharmacotherapy 13 (1993): s23-8[8] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[9] Mizuki Y, Fujiwara I, Yamaguchi T "Pharmacokinetic interactions related to the chemical structures of fluoroquinolones." J Antimicrob Chemother 37 Suppl A (1996): 41-55[10] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86263, 'Didanosine', 'Stavudine', 'Major', 'The concomitant use of the nucleoside reverse transcriptase inhibitors (NRTIs), didanosine (ddI) and stavudine (d4T), has been associated with an increased risk of certain toxicities related to mitochondrial damage, and the addition of hydroxyurea to the combination may further increase that risk. Specifically, concurrent use of ddI and d4T has been associated with an increased risk of pancreatitis, several cases of which have been fatal. Additionally, the risk and severity of NRTI-related peripheral neuropathy may be additively or synergistically increased. Although rare, the syndrome of lactic acidosis and severe hepatomegaly with steatosis (with or without pancreatitis) may also occur more frequently with this combination than with antiretroviral regimens containing either drug alone or other combinations of NRTIs. Fatal lactic acidosis has been reported in pregnant women receiving ddI and d4T with other antiretroviral agents, although it is unclear if pregnancy is a specific risk factor.', NULL, 'Close observation for signs and symptoms of toxicity is recommended if ddI and d4T must be used together, particularly in patients with advanced HIV disease and/or elderly patients.', NULL, 'Close observation for signs and symptoms of toxicity is recommended if ddI and d4T must be used together, particularly in patients with advanced HIV disease and/or elderly patients. Both drugs should be suspended if pancreatitis is suspected, and ddI discontinued permanently if pancreatitis is confirmed. Reinstitution of d4T, if necessary, should be undertaken with caution following recovery. Prompt withdrawal of these drugs is also essential in patients who develop peripheral neuropathy to avoid irreversible damage. Therapy may be reinstituted following resolution of neuropathy symptoms, but reduced dosages are recommended. Likewise, treatment should be suspended in any patient who develops clinical or laboratory findings suggestive of lactic acidosis or pronounced hepatotoxicity, and permanent discontinuation considered if the syndrome is confirmed. In pregnant women, the manufacturers recommend that the combination of ddI and d4T be used with caution and only if potential benefit clearly outweighs the risks. Patients should be advised to seek medical attention promptly if symptoms of toxicity occur such as nausea, vomiting, abdominal pain/distention, fatigue, unexplained weight loss, tachypnea, dyspnea, motor weakness, numbness, tingling, and pain in hands and feet.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86364/', '[1] Havlir DV, Gilbert PB, Bennett K, et al. "Effects of treatment intensification with hydroxyurea in HIV-infected patients with virologic suppression." AIDS 15 (2001): 1379-88[2] Moore RD, Keruly JC, Chaisson RE "Incidence of pancreatitis in HIV-infected patients receiving nucleoside reverse transcriptase inhibitor drugs." Aids 15 (2001): 617-20[3] Moore RD, Wong WME, Keruly JC, McArthur JC "Incidence of neuropathy in HIV-infected patients on monotherapy versus those on combination therapy with didanosine, stavudine and hydroxyurea." Aids 14 (2000): 273-8[4] Boubaker K, Flepp M, Sudre P, et al. "Hyperlactatemia and Antiretroviral Therapy: The Swiss HIV Cohort Study." Clin Infect Dis 33 (2001): 1931-7[5] Kronenberg A, Riehle HM, Gunthard HF "Liver failure after long-term nucleoside antiretroviral therapy." Lancet 358 (2001): 759-601[6] Lonergan JT, Behling C, Pfander H, Hassanein TI, Mathews WC "Hyperlactatemia and hepatic abnormalities in 10 human immunodeficiency virus-infected patients receiving nucleoside analogue combination regimens." Clin Infect Dis 31 (2000): 162-6[7] Rutschmann OT, Opravil M, Iten A, Malinverni R, Vernazza PL, Bucher HC, Bernasconi E, Sudre P, Leduc D, Yerly S, Perrin LH, "A placebo-controlled trial of didanosine plus stavudine, with and without hydroxyurea, for HIV infection." AIDS 12 (1998): f71-7[8] Cepeda JA, Wilks D "Excess peripheral neuropathy in patients treated with hydroxyurea plus didanosine and stavudine for HIV infection." Aids 14 (2000): 332-3[9] "Product Information. Zerit (stavudine)." Bristol-Myers Squibb, Princeton, NJ.[10] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[11] Coghlan ME, Sommadossi JP, Jhala NC, Many WJ, Saag MS, Johnson VA "Symptomatic lactic acidosis in hospitalized antiretroviral-treated patients with human immunodeficiency virus infection: a report of 12 cases." Clin Infect Dis 33 (2001): 1914-21[12] Rutschmann OT, Vernazza PL, Bucher HC, Opravil M, Ledergerber B, Telenti A, Malinverni R, Bernasconi E, Fagard C, Leduc D, Perr "Long-term hydroxyurea in combination with didanosine and stavudine for the treatment of HIV-1 infection." Aids 14 (2000): 2145-51[13] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.', 1767365205);
