INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86279, 'Didanosine', 'Trovafloxacin', 'Moderate', 'INTERVAL: Concomitant administration with didanosine buffered tablets or pediatric oral solution may reduce the oral bioavailability of trovafloxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine.', NULL, 'Trovafloxacin should be administered at least 2 hours before or 2 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', NULL, 'Trovafloxacin should be administered at least 2 hours before or 2 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86380/', '[1] "Product Information. Trovan (trovafloxacin)." Pfizer US Pharmaceuticals, New York, NY.[2] Polk RE "Drug-drug interactions with ciprofloxacin and other fluoroquinolones." Am J Med 87 (1989): s76-81[3] Damle BD, Mummaneni V, Kaul S, Knupp C "Lack of Effect of Simultaneously Administered Didanosine Encapsulated Enteric Bead Formulation (Videx EC) on Oral Absorption of Indinavir, Ketoconazole, or Ciprofloxacin." Antimicrob Agents Chemother 46 (2002): 385-91