INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86280, 'Didanosine', 'Valganciclovir', 'Moderate', 'The coadministration of didanosine and ganciclovir may result in significantly elevated plasma concentrations of didanosine, while the pharmacokinetics of ganciclovir are minimally affected. Other studies have reported similar or even greater alterations with both intravenous and oral ganciclovir. The mechanism is unknown but may involve competitive inhibition of didanosine renal tubular secretion or increased bioavailability of didanosine by ganciclovir. Caution is advised if didanosine must be used concomitantly with ganciclovir or its prodrug, valganciclovir.', NULL, 'Close monitoring for toxicities associated with didanosine such as pancreatitis, lactic acidosis, and peripheral neuropathy is recommended during concurrent therapy.', NULL, 'Close monitoring for toxicities associated with didanosine such as pancreatitis, lactic acidosis, and peripheral neuropathy is recommended during concurrent therapy. Patients should be advised to promptly notify their physician if they experience possible symptoms of toxicity (e.g., abdominal pain, nausea, vomiting, diarrhea, weakness, dizziness, lightheadedness, irregular heartbeat, or numbness, pain, or tingling in the hands and feet).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86381/', '[1] Taburet AM, Singlas E "Drug interactions with antiviral drugs." Clin Pharmacokinet 30 (1996): 385-401[2] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[3] Trapnell CB, Cimoch P, Gaines K, Jung D, Hale M, Lavelle J "Altered didanosine pharmacokinetics with concomitant