INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86286, 'Didanosine', 'Zalcitabine', 'Moderate', 'Zalcitabine can cause peripheral neuropathy in up to one-third of patients with advanced HIV disease, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Zalcitabine-related peripheral neuropathy is a sensorimotor neuropathy characterized initially by numbness and burning dysesthesia involving the distal extremities. These symptoms may be followed by sharp shooting pains or severe continuous burning pain if the drug is not withdrawn, and progress to severe pain requiring narcotic analgesics. The neuropathy is potentially irreversible. However, with prompt discontinuation of zalcitabine, it is usually slowly reversible, although symptoms may initially progress following discontinuation.', NULL, 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible.', NULL, 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible. Otherwise, careful monitoring is recommended for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness in the extremities, particularly in patients with a low CD4 cell count or diabetes. Since the development of peripheral neuropathy appears to be dose-related, the recommended dosage of zalcitabine should not be exceeded. Patients should be advised to promptly discontinue zalcitabine therapy and contact their physician if neuropathy develops. Therapy may be reinstituted following resolution of neuropathy symptoms, but dosage should be reduced to one-half the initially recommended dosage. Zalcitabine should be permanently discontinued in patients who develop severe peripheral neuropathy during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86387/', '[1] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86287, 'Diethylpropion', 'Acetohexamide', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86388/', '[1] "Product Information. Xenical (orlistat)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[3] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.[4] "Product Information. Belviq (lorcaserin)." Eisai Inc, Teaneck, NJ.[5] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[6] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[7] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[8] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol