INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86291, 'Diethylpropion', 'Deserpidine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86392/', '[1] Gerkens JF, McCulloch MW, Wilson J "Mechanism of the antagonism between guanethidine and dexamphetamine." Br J Pharmacol 35 (1969): 563-72[2] Flegin OT, Morgan DH, Oates JA, Shand DG, Turner P "The mechanism of the reversal of the effect of guanethidine by amphetamines in cat and man." Br J Pharmacol 39 (1970): p253[3] Gulati OD, Dave BT, Gokhale SD, Shah KM "Antagonism of adrenergic neuron blockade in hypertensive subjects." Clin Pharmacol Ther 7 (1966): 510-4[4] Sherman GP, Walton CA "Adrenergic transmission and drug interaction." J Am Pharm Assoc 15 (1975): 86-90[5] Obianwu HO "Some studies on the mechanism by which d-amphetamine antag