    ) VALUES (1375553, 'Busulfan', 'Oxaliplatin', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals. Dosing adjustments may be necessary. The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
