INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86525, 'Digoxin', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of the efflux transporter P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of P-gp activity by berotralstat.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of P-gp, particularly those with a narrow therapeutic index.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of P-gp, particularly those with a narrow therapeutic index. Clinical and laboratory monitoring should be considered following the initiation or discontinuation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86626/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86526, 'Digoxin', 'Bismuth subsalicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', NULL, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86627/', '[1] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[2] Jorgensen HS, Christensen HR, Kampmann JP "Interaction between digoxin and indomethacin or ibuprofen." Br J Clin Pharmacol 31 (1991): 108-10[3] Finch MB, Johnston GD, Kelly JG, McDevitt DG "Pharmacokinetics of digoxin alone and in the presence of indomethacin therapy." Br J Clin Pharmacol 17 (1984): 353-5[4] Marcus FI "Pharmacokinetic interactions between digoxin and other drugs." J Am Coll Cardiol 5 (1985): a82-90[5] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85[6] Brown DD, Schmid