INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86540, 'Digoxin', 'Cytarabine', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', NULL, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86641/', '[1] Kuhlmann J, Wilke J, Rietbrock N "Cytostatic drugs are without significant effect on digitoxin plasma level and renal excretion." Clin Pharmacol Ther 32 (1982): 646-51[2] Bjornsson TD, Huang AT, Roth P, Jacob DS, Christenson R "Effects of high-dose cancer chemotherapy on the absorption of digoxin in two different formulations." Clin Pharmacol Ther 39 (1986): 25-8[3] "Product Information. Lanoxicaps (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[4] Kuhlmann J, Zilly W, Wilke J "Effects of cytostatic drugs on plasma level and renal excretion of beta-acetyldigoxin." Clin Pharmacol Ther 30 (1981): 518-27[5] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[6] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-5