INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86547, 'Dofetilide', 'Digoxin', 'Moderate', 'Concomitant administration of digoxin and dofetilide has been associated with a higher occurrence of torsade de pointes arrhythmia than dofetilide alone. It is not clear whether this finding represents an interaction between the drugs or the presence of more severe structural heart disease, a known risk factor for arrhythmia, in patients on digoxin.', NULL, 'Clinicians should be aware of the potential for increased risk of torsade de pointes when dofetilide is administered in patients treated with digoxin.', NULL, 'Clinicians should be aware of the potential for increased risk of torsade de pointes when dofetilide is administered in patients treated with digoxin.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86648/', '[1] Kleinermans D, Nichols DJ, Dalrymple I "Effect of Dofetilide on the pharmacokinetics of digoxin." Am J Cardiol 87 (2001): 248+[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[3] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[4] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6[5] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86548, 'Dolasetron', 'Digoxin', 'Major', 'Dolasetron has been shown to cause dose-dependent prolongation of the PR and QRS intervals. There have been reports of second- and third-degree atrioventricular block, cardiac arrest, and serious ventricular arrhythmias including fatalities in both adult and pediatric patients.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors. An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat. Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86649/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86549, 'Donepezil', 'Digoxin', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86650/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis 