INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86563, 'Ephedrine', 'Digoxin', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', NULL, 'Caution should be exercised if these two drugs are coadministered.', NULL, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86664/', '[1] "Product Information. Claritin-D (loratadine-pseudoephedrine)." Schering-Plough, Liberty Corner, NJ.[2] "Product Information. Epinephrine Hydrochloride Injection (epinephrine)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Lanoxin (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[4] "Product Information. Isuprel (isoproterenol)." Sanofi Winthrop Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Allegra-D (fexofenadine-pseudoephedrine)." Chattem Consumer Products, Chattanooga, TN.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.[9] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[10] "Product Information. Didrex (benzphetamine)" 