INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86589, 'Gentamicin', 'Digoxin', 'Minor', 'Animal data show that serum digoxin levels increase after administration of nontoxic doses of gentamicin. The digoxin doses were five times that usually recommended for healthy human adults with normal renal function (on a per kg basis). Signs of digoxin toxicity were not apparent until toxic doses of gentamicin were given. These data may not apply to humans, and the clinical relevance is not clear. At this time, no significant clinical interaction is suspected in humans.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86690/', '[1] Staneva-Stoytcheva D, Kristeva E, Prodanova K "Some pharmacokinetic and pharmacodynamic interactions between digoxin and gentamicin." Eur J Drug Metab Pharmacokinet 17 (1992): 1-8[2] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[3] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86590, 'Ginseng', 'Digoxin', 'Moderate', 'Asian and Siberian ginseng may interfere with digoxin either pharmacodynamically or with its monitoring via certain immunoassays. The interaction has been observed in vitro as well as in nondigoxin-treated mice that were fed ginseng and is thought to be due to substances in the ginseng that structurally resemble cardiac glycosides. Serum pools prepared from samples from patients receiving digoxin and then supplemented with Asian or Siberian ginseng also demonstrated interference.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Use of ginseng should probably be avoided in patients treated with cardiac glycosides.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86691/', '[1] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[2] McRae S "Elevated serum digoxin levels in a patient taking digoxin and Siberian ginseng." CMAJ 155 (1996): 293-5[3] Dasgupta A, Wu S, Actor J, Olsen M, Wells A, Datta P "Effect of Asian and Siberian ginseng on serum digoxin measurement by five digoxin immunoassays. Significant variation in digoxin-like immunoreactivity among commercial ginsengs." Am J Clin Pathol 119 (2003): 298-303', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86591, 'Glasdegib', 'Digoxin', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86692/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
