INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86611, 'Digoxin', 'Labetalol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86712/', '[1] Lowenthal DT, Porter RS, Saris SD, Bies CM, Slegowski MB, Staudacher A "Clinical pharmacology, pharmacodynamics and interactions with esmolol." Am J Cardiol 56 (1985): f14-8[2] "Product Information. Zebeta (bisoprolol)." Lederle Laboratories, Wayne, NJ.[3] Watt DA "Sensitivity to propranolol after digoxin intoxication." Br Med J 2 (1968): 413-4[4] De Mey C, Brendel E, Enterling D "Carvedilol increases the systemic bioavailability of oral digoxin." Br J Clin Pharmacol 29 (1990): 486-90[5] Takara K, Kakumoto M, Tanigawara Y, Funakoshi J, Sakaeda T, Okumura K "Interaction of digoxin with antihypertensive drugs via MDR 1." Life Sci 70 (2002): 1491-1500[6] LeWinter MM, Crawford MH, O''Rourke RA, Karliner JS "The effects of oral propranolol, digoxin and combination therapy on the resting and exercise electrocardiogram." Am Heart J 93 (1977): 202-9[7] Wermeling DP, Field CJ, Smith DA, Chandler MH, Clifton GD, Boyle DA "Effects of long-term oral carvedilol on the steady-state pharmacokinetics of oral digoxin in patients with mild to moderate hypertension." Pharmacotherapy 14 (1994): 600-6[8] "Product Information. Coreg (carvedilol)." SmithKline Beecham, Philadelphia, PA.[9] Ratnapalan S, Griffiths K, Costei AM, Benson L, Koren G "Digoxin-carvedilol interactions in children." J Pediatr 142 (2003): 572-574[10] Eichhorn EJ, Lukas MA, Wu B, Shusterman N "Effect of concomitant digoxin and carvedilol therapy of mortality and morbidity in patients with chronic heart failure." Am J Cardiol 86 (2000): 1032-5[11] "Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[12] Lowenthal DT, Porter RS, Achari R, Turlapaty P, Laddu AR, Matier WL "Esmolol-digoxin drug interaction" J Clin Pharmacol 27 (1987): 561-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86612, 'Digoxin', 'Lacosamide', 'Moderate', 'Lacosamide has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, digoxin, atazanavir, mefloquine) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block, second-degree or higher AV block and sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. An ECG should be obtained in these patients before initiating lacosamide and after titration to steady-state.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86713/', '[1] "Product Information. Vimpat (lacosamide)." UCB Pharma Inc, Smyrna, GA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86613, 'Digoxin', 'Lanreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', NULL, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86714/', '[1] "Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[2] "Product Information. Somatuline Depot (lanreotide)." Ipsen Inc, Milford, MA.[3] "Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[4] "Product Information. Somatuline Depot (lanreotide)." Ipsen Inc, Milford, MA.[5] Katz MD, Erstad BL "Octreotide, a new somatostatin analogue." Clin Pharm 8 (1989): 255-73[6] Ho PJ, Boyajy LD, Greenstein E, Barkan AL "Effect of chronic octreotide treatment on intestinal absorption in patients with acromegaly." Dig Dis Sci 38 (1993): 309-15[7] Landgraf R, Landgraf-Leurs MM, Nusser J, et al "Effect of somatostatin analogue (SMS201-995) on cyclosporine levels." Transplantation 44 (1987): 724-5', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86614, 'Digoxin', 'Lansoprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', NULL, 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86715/', '[1] Oosterhuis B, Jonkman JH, Andersson T, Zuiderwijk PB, Jedema JN "Minor effect of multiple dose omeprazole on the pharmacokinetics of digoxin after a single oral dose." Br J Clin Pharmacol 32 (1991): 569-72[2] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[3] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[4] Kiley CA, Cragin DJ, Roth BJ "Omeprazole-associated digoxin toxicity." South Med J 100 (2007): 400-2[5] "Product Information. Kapidex (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[6] "Product Information. Protonix (pantoprazole)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Andersson T "Omeprazole drug interaction studies." Clin Pharmacokinet 21 (1991): 195-212[8] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[9] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[10] Le GH, Schaefer MG, Plowman BK, et al. "Assessment of potential digoxin-rabeprazole interaction after formulary conversion of proton-pump inhibitors." Am J Health Syst Pharm 60 (2003): 1343-5[11] "Product Information. Prilosec (omeprazole)." Merck & Co, Inc, West Point, PA.[12] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[13] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86615, 'Digoxin', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: Theoretically, lanthanum carbonate may chelate with certain drugs in the gastrointestinal tract, resulting in reduced oral bioavailability of those drugs during coadministration.', NULL, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.', NULL, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.g., ACE inhibitors, beta blockers, bisphosphonates, coumarin derivatives, digitalis glycosides, fluoroquinolones, iron, phenytoin, rifampin, tetracyclines, thyroid preparations, valproic acid) should not be taken within 2 hours of administration of lanthanum carbonate according to the product labeling.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86716/', '[1] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Fosrenol (lanthanum carbonate)." Shire US Inc, Florence, KY.[5] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86616, 'Digoxin', 'Lapatinib', 'Major', 'Coadministration with lapatinib may significantly increase the plasma concentrations of digoxin. The proposed mechanism is lapatinib inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin.', NULL, 'Caution is advised if digoxin is used in combination with lapatinib.', NULL, 'Caution is advised if digoxin is used in combination with lapatinib. Serum digoxin levels and pharmacologic effects should be closely monitored during coadministration and following the discontinuation of lapatinib, and the digoxin dosage adjusted as necessary. If digoxin serum concentration is above 1.2 ng/mL, the digoxin dosage should be reduced by one-half. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86717/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[4] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
