INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86623, 'Digoxin', 'Licorice', 'Moderate', 'Chronic use of licorice may potentiate the risk of digitalis toxicity and other cardiovascular complications in patients treated with cardiac glycosides. Glycyrrhizic acid, a component of licorice, can induce hypokalemia, hypernatremia, edema, hypertension, and suppression of the renin-aldosterone system by virtue of its mineralocorticoid activity. In digitalized patients, electrolyte imbalance such as hypokalemia and hypomagnesemia can lead to arrhythmias, while other chronic effects of glycyrrhizic acid can compromise overall cardiovascular condition.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, chronic use of licorice and licorice-containing products should be avoided in patients treated with cardiac glycosides.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86724/', '[1] Elinav E, Chajek-Shaul T "Licorice consumption causing severe hypokalemic paralysis." Mayo Clin Proc 78 (2003): 767-8[2] Nielsen I, Pedersen RS "Life-threatening hypokalaemia caused by liquorice ingestion." Lancet 1 (1984): 1305[3] de Klerk GJ, Nieuwenhuis MG, Beutler JJ "Lesson of the week: hypokalaemia and hypertension associated with use of liquorice flavoured chewing gum." BMJ 314 (1997): 731[4] Rosseel M, Schoors D "Chewing gum and hypokalaemia." Lancet 341 (1993): 175[5] Clyburn EB, DiPette DJ "Hypertension induced by drugs and other substances." Semin Nephrol 15 (1995): 72-86[6] Farese RV, B