INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86654, 'Digoxin', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86755/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86655, 'Digoxin', 'Mifepristone', 'Moderate', 'Coadministration with mifepristone may increase the plasma concentrations of digoxin. The proposed mechanism is mifepristone inhibition of P-glycoprotein-mediated intestinal efflux of digoxin. When digoxin 0.125 mg once daily was administered with mifepristone 1200 mg once daily for 10 days in study subjects, mean digoxin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 1.6- and 1.4-fold, respectively, compared to administration of digoxin alone.', NULL, 'Caution is advised if digoxin must be used concomitantly with mifepristone.', NULL, 'Caution is advised if digoxin must be used concomitantly with mifepristone. Dosage adjustments as well as clinical and laboratory monitoring are appropriate for digoxin whenever mifepristone is added to or withdrawn from therapy. The manufacturer recommends that plasma digoxin concentration be measured after 1 to 2 weeks of concomitant use and periodically as necessary thereafter. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86756/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[5] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86656, 'Digoxin', 'Milnacipran', 'Moderate', 'Use of milnacipran concomitantly with digoxin may be associated with potentiation of adverse hemodynamic effects. Inhibition of norepinephrine and serotonin reuptake by milnacipran can lead to cardiovascular effects including sustained increases in blood pressure and heart rate. However, effects of milnacipran in patients with significant hypertension, arrhythmia, or cardiac disease have not been systematically evaluated.', NULL, 'Coadministration of milnacipran and intravenous digoxin should be avoided.', NULL, 'Coadministration of milnacipran and intravenous digoxin should be avoided. Caution is advised during coadministration with oral digoxin therapy. Blood pressure and heart rate should be measured prior to initiating milnacipran and periodically throughout treatment. A dosage reduction or discontinuation should be considered in patients who experience a sustained increase in blood pressure or heart rate while receiving milnacipran.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86757/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86657, 'Digoxin', 'Minocycline', 'Moderate', 'Tetracyclines may increase serum levels of orally administered digoxin in about 10% of the population. The mechanism may be related to changes in intestinal flora that alter the absorption of digoxin. The risk of an interaction may be less with digoxin solution in capsules because absorption occurs in the upper GI tract.', NULL, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended.', NULL, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended. Digoxin dosage reductions may be required. Patients should be advised to notify their physicians if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86758/', '[1] Lindenbaum J, Rund DG, Butler VP Jr, Tse-Eng D, Saha JR "Inactivation of digoxin by the gut flora: reversal by antibiotic therapy." N Engl J Med 305 (1981): 789-94[2] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[3] Lindenbaum J, Tse-Eng D, Butler VP, Rund DG "Urinary excretion of reduced metabolites of digoxin." Am J Med 71 (1981): 67-74[4] "Product Information. Lanoxicaps (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86658, 'Digoxin', 'Mirabegron', 'Moderate', 'Coadministration with mirabegron may increase the serum concentrations of digoxin. The exact mechanism of interaction has not been established. However, since both agents are substrates for the P-glycoprotein efflux transporter, competitive inhibition is possible.', NULL, 'The lowest dosage of digoxin should initially be considered when prescribed in combination with mirabegron.', NULL, 'The lowest dosage of digoxin should initially be considered when prescribed in combination with mirabegron. Pharmacologic response and serum digoxin levels should be monitored, and digoxin dosage adjusted as needed to attain desired clinical effect.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86759/', '[1] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.[2] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[3] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6[4] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.', 1767363022);
