INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86660, 'Digoxin', 'Moexipril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', NULL, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86761/', '[1] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[2] Douste-Blazy P, Blanc M, Montastruc JL, et al "Is there any interaction between digoxin and enalapril?" Br J Clin Pharmacol 22 (1986): 752-3[3] Cleland JG, Dargie HJ, Pettigrew A, Gillen G, Robertson JI "The effects of captopril on serum digoxin and urinary urea and digoxin clearances in patients with congestive heart failure." Am Heart J 112 (1986): 130-5[4] Doering W, Maass L, Irmisch R, Konig E "Pharmacokinetic interaction study with ramipril and digoxin in healthy volunteers." Am J Cardiol 59 (1987): d60-4[5] Kromer EP, Elsner D, Riegger GA "Digoxin, converting-enzyme inhibition (quinapril), and the combination in patients with congestive heart failure funct