INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86676, 'Digoxin', 'Oxybutynin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86777/', '[1] Brown DD, Schmid J, Long RA, Hull JH "A steady-state evaluation of the effects of propantheline bromide and cholestyramine on the bioavailability of digoxin when administered as tablets or capsules." J Clin Pharmacol 25 (1985): 360-4[2] Manninen V, Apajalahti A, Simonen H, Reissell P "Effect of propantheline and metoclopramide on absorption of digoxin." Lancet 1 (1973): 1118-9[3] Johnson BF, O''Grady J, Bye C "The influence of digoxin particle size on absorption of digoxin and the effect of propantheline and metoclopramide." Br J Clin Pharmacol 5 (1978): 465-7[4] Binnion PF, McDermott M, LeSher D "Bioavailability of digoxin." Lancet 1 (1973): 1118[5] "Product Information. Lanoxicaps (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[6] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[7] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86677, 'Digoxin', 'Oxytetracycline', 'Moderate', 'Tetracyclines may increase serum levels of orally administered digoxin in about 10% of the population. The mechanism may be related to changes in intestinal flora that alter the absorption of digoxin. The risk of an interaction may be less with digoxin solution in capsules because absorption occurs in the upper GI tract.', NULL, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended.', NULL, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended. Digoxin dosage reductions may be required. Patients should be advised to notify their physicians if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86778/', '[1] Lindenbaum J, Rund DG, Butler VP Jr, Tse-Eng D, Saha JR "Inactivation of digoxin by the gut flora: reversal by antibiotic therapy." N Engl J Med 305 (1981): 789-94[2] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[3] Lindenbaum J, Tse-Eng D, Butler VP, Rund DG "Urinary excretion of reduced metabolites of digoxin." Am J Med 71 (1981): 67-74[4] "Product Information. Lanoxicaps (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86678, 'Digoxin', 'Ozanimod', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86779/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86679, 'Digoxin', 'Pancuronium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', NULL, 'Continuous cardiac monitoring is recommended during coadministration.', NULL, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86780/', '[1] "Product Information. Lanoxin (digoxin)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86680, 'Digoxin', 'Pantoprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', NULL, 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86781/', '[1] Oosterhuis B, Jonkman JH, Andersson T, Zuiderwijk PB, Jedema JN "Minor effect of multiple dose omeprazole on the pharmacokinetics of digoxin after a single oral dose." Br J Clin Pharmacol 32 (1991): 569-72[2] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[3] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[4] Kiley CA, Cragin DJ, Roth BJ "Omeprazole-associated digoxin toxicity." South Med J 100 (2007): 400-2[5] "Product Information. Kapidex (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[6] "Product Information. Protonix (pantoprazole)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Andersson T "Omeprazole drug interaction studies." Clin Pharmacokinet 21 (1991): 195-212[8] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[9] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[10] Le GH, Schaefer MG, Plowman BK, et al. "Assessment of potential digoxin-rabeprazole interaction after formulary conversion of proton-pump inhibitors." Am J Health Syst Pharm 60 (2003): 1343-5[11] "Product Information. Prilosec (omeprazole)." Merck & Co, Inc, West Point, PA.[12] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[13] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86681, 'Digoxin', 'Parathyroid hormone', 'Major', 'Parathyroid hormone may alter the effects of digitalis glycosides. The inotropic effects of digitalis glycosides are affected by serum calcium levels. Since parathyroid hormone causes a transient increase in serum calcium, it can potentiate the risk of digitalis toxicity if hypercalcemia develops. On the other hand, digitalis efficacy may be reduced if hypocalcemia occurs. Severe cases of both hypercalcemia and hypocalcemia have been reported during treatment with parathyroid hormone. The risk of hypercalcemia is greatest when starting or increasing the dose of parathyroid hormone, whereas the risk of hypocalcemia is greatest when parathyroid hormone is withheld, missed, or abruptly discontinued.', NULL, 'Serum calcium and digitalis levels should be closely monitored during parathyroid hormone therapy, and the digitalis dosage adjusted as necessary.', NULL, 'Serum calcium and digitalis levels should be closely monitored during parathyroid hormone therapy, and the digitalis dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86782/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] "Product Information. Natpara (parathyroid hormone)." NPS Pharmaceuticals, Bedminster, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86682, 'Digoxin', 'Paricalcitol', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', NULL, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', NULL, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86783/', '[1] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[2] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.[5] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86683, 'Digoxin', 'Paromomycin', 'Minor', 'Oral neomycin has been shown to decrease the absorption of oral digoxin. The effects of oral paromomycin and kanamycin are not known. The proposed mechanism is a neomycin-induced malabsorption syndrome resulting in reduced absorption of drugs. This effect may be offset by a subsequent decrease in digoxin metabolism. During concomitant therapy with any of these antibiotics, digoxin serum concentrations and efficacy should be monitored. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86784/', '[1] Lindenbaum J, Tse-Eng D, Butler VP, Rund DG "Urinary excretion of reduced metabolites of digoxin." Am J Med 71 (1981): 67-74[2] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[3] Lindenbaum J, Maulitz RM, Butler VP "Inhibition of digoxin absorption by neomycin." Gastroenterology 71 (1976): 399-404[4] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[5] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767363022);
