INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86685, 'Digoxin', 'Pasireotide', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', NULL, 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', NULL, 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86786/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Signifor (pasireotide)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86686, 'Digoxin', 'Pectin', 'Moderate', 'INTERVAL: Concurrent administration of kaolin-pectin may decrease the oral bioavailability of digitalis glycosides. The mechanism of interaction is unknown.', NULL, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible.', NULL, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible. The same precaution may be applicable during therapy with digitoxin, although clinical data are lacking.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86787/', '[1] Rodin SM, Johnson BF "Pharmacokinetic interactions with digoxin." Clin Pharmacokinet 15 (1988): 227-44[2] Allen MD, Greenblatt DJ, Harmatz JS, Smith TW "Effect of magnesium--aluminum hydroxide and kaolin--pectin on absorption of digoxin from tablets and capsules." J Clin Pharmacol 21 (1981): 26-30[3] Albert KS, Ayres JW, DiSanto AR, Weidler DJ, Sakmar E, Hallmark MR, Stoll RG, DeSante KA, Wagner JG "Influence of kaolin--pectin suspension on digoxin bioavailability." J Pharm Sci 67 (1978): 1582-6[4] Marcus FI "Pharmacokinetic interactions between digoxin and other drugs." J Am Coll Cardiol 5 (1985): a82-90[5] Brown DD, Juhl RP "Decreased bioavailability of digoxin due to antacids and kaolin-pectin." N Engl J Med 295 (1976): 1034-7', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86687, 'Digoxin', 'Penbutolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86788/', '[1] Lowenthal DT, Porter RS, Saris SD, Bies CM, Slegowski MB, Staudacher A "Clinical pharmacology, pharmacodynamics and interactions with esmolol." Am J Cardiol 56 (1985): f14-8[2] "Product Inf