INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86695, 'Digoxin', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may reduce plasma concentrations of substrates of CYP450 2C, P-glycoprotein (P-gp), and uridine diphosphate glucuronosyltransferases (UGTs). Based on in vitro data, pitolisant and its main metabolites may induce CYP450 2C, P-gp, and UGTs.', NULL, 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs.', NULL, 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs. Clinical and laboratory monitoring may be appropriate for some drugs whenever pitolisant is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86796/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86696, 'Digoxin', 'Polythiazide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86797/', '[1] Semple P, Tilstone WJ, Lawson DH "Furosemide and urinary digoxin clearance." N Engl J Med 293 (1975): 612-3[2] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[3] Whang R, Oei TO, Watanabe A "Frequency of hypomagnesemia in hospitalized patients receiving digitalis." Arch Intern Med 145 (1985): 655-6[4] Macolic V, Vrhovac B "Pharmacokinetics and interactions of digoxin, theophylline and furosemide in diseases with edema." Int J Clin Pharmacol Ther Toxicol 31 (1993): 6-11[5] McAllister RG, Howell SM, Gomer MS, Selby JB "Effect of intravenous furosemide on the renal excretion of digoxin." J Clin Pharmacol 16 (1976): 110-7[6] Waldorff S