INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (86827, 'Eluxadoline', 'Dihydroergotamine', 'Moderate', 'It is not known whether, and to what extent, eluxadoline may inhibit CYP450 3A4 and may interact with substrates of the isoenzyme. In vitro studies have shown that eluxadoline is not an inhibitor of CYP450 3A4 at clinically relevant systemic concentrations, but they were not adequate to rule out inhibition of CYP450 3A4 in the gut by eluxadoline. Therefore, the potential for eluxadoline to increase plasma concentrations of oral drugs that are metabolized by CYP450 3A4 should be considered.', NULL, 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges.', NULL, 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, and macrolide immunosuppressants.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86928/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[3] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[4] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[5] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam a