    ) VALUES (1375612, 'Butalbital', 'Bedaquiline', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2.', NULL, 'Caution is advised when bedaquiline is used in combination with inducers of CYP450 3A4 due to the potential for reduced antimycobacterial effect.', NULL, 'Caution is advised when bedaquiline is used in combination with inducers of CYP450 3A4 due to the potential for reduced antimycobacterial effect.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
