    ) VALUES (1375618, 'Canagliflozin', 'Butalbital', 'Moderate', 'Coadministration with uridine diphosphoglucuronate-glucuronosyltransferase (UGT) inducers such as rifampin, phenytoin, phenobarbital, and ritonavir may decrease area under the curve (AUC) of canagliflozin by up to 51%.', NULL, 'If coadministration with a UGT inducer is required, a higher dose of canagliflozin may be needed based on tolerability, glycemic control, and renal function.', NULL, 'If coadministration with a UGT inducer is required, a higher dose of canagliflozin may be needed based on tolerability, glycemic control, and renal function. Monitor glycemic control and refer to the product labeling for specific guidelines on dosing adjustments.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
