INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87525, 'Diphenhydramine', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', 'Lidocaine, Clemastine, Tetracaine, Mepyramine, Tripelennamine, Pheniramine, Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87626/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Photofrin (porfimer)." Sanofi Winthrop Pharmaceuticals, New York, NY.[3] Blakely KM, Drucker AM, Rosen CF "Drug-induced photosensitivityan update: Culprit drugs, prevention and management." Drug Saf 42 (2019): 827-47[4] Hoffman GA, Gradl G, Schulz M, Haidinger G, Tanew A, Weber B "The frequency of photosensitizing drug dispensings in Austria and Germany: A correlation with their photosensitizing potential based on pu