INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87796, 'Dipivefrin (ophthalmic)', 'Midodrine', 'Moderate', 'The pressor effects of midodrine may be potentiated by the concomitant use of other agents that cause vasoconstriction, including sympathomimetics and ergot alkaloids. Midodrine is an alpha-adrenergic agonist and may cause marked elevation of supine arterial blood pressure, or supine hypertension.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure. Supine and sitting blood pressures should be closely monitored. Supine hypertension can often be controlled by keeping the patient from being fully supine, for example, sleeping with the head of the bed elevated. Taking the last daily dose of midodrine 3 to 4 hours before bedtime may also help to minimize nighttime supine hypertension. Patients should be advised to contact their physician immediately if they experience symptoms of supine hypertension such as cardiac awareness, pounding in the ears, headache, and blurred vision. Midodrine should be discontinued if supine hypertension persists.', 'Synergism', 'Dobutamine, Norepinephrine, Droxidopa, Amrinone, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Fenoldopam, Metaraminol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87897/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87797, 'Dipivefrin (ophthalmic)', 'Milnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Esketamine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87898/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, P