INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376234, 'Butorphanol', 'Troglitazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, methadone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317475, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376235, 'Butorphanol', 'Troleandomycin', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of butorphanol according to the product labeling. Increased butorphanol concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease butorphanol plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to butorphanol.', 'DDInter', 1767317475, 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath. When discontinuing CYP450 3A4 inhibitors, monitor patients closely at frequent intervals and consider increasing the opioid dosage if needed to maintain adequate analgesia or if symptoms of opioid withdrawal occur.', 'Metabolism', 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', NULL, NULL, 'Major', 'pharmacodynamic');
