INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376262, 'Cabazitaxel', 'Berotralstat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to cabazitaxel should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the cabazitaxel dosage adjusted as necessary. Regular monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience potential signs and symptoms of cabazitaxel toxicity such as infection, fever, chills, diarrhea, unusual bruising or bleeding, and burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376263, 'Cabazitaxel', 'Bifidobacterium longum infantis', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
