INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376265, 'Butabarbital', 'Cabazitaxel', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme. However, no formal drug interaction studies have been conducted.', 'DDInter', 1767317475, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown. Caution is advised if they are used with cabazitaxel.', 'Metabolism', 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376266, 'Canakinumab', 'Cabazitaxel', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376267, 'Candida albicans', 'Cabazitaxel', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376268, 'Capmatinib', 'Cabazitaxel', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as digoxin and rosuvastatin. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of intestinal P-gp and/or BCRP efflux transport proteins.', 'DDInter', 1767317475, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
