INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376379, 'Cabazitaxel', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', 'DDInter', 1767317475, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
