INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376402, 'Cabazitaxel', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', 'DDInter', 1767317475, 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376403, 'Cabazitaxel', 'Pegfilgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 1767317475, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
