INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376419, 'Cabazitaxel', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 1767317475, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376420, 'Cabazitaxel', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to cabazitaxel should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the cabazitaxel dosage adjusted as necessary. Regular monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience potential signs and symptoms of cabazitaxel toxicity such as infection, fever, chills, diarrhea, unusual bruising or bleeding, and burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
