INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88206, 'Methadone', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 such as ketoconazole may significantly increase the plasma concentrations of methadone, which is primarily metabolized by the isoenzyme. High dosages and plasma levels of methadone have been associated with QT interval prolongation and torsade de pointes arrhythmia. Ketoconazole itself can also prolong the QT interval, thus an additive pharmacodynamic effect with methadone may occur as well.', NULL, 'Coadministration of methadone with ketoconazole is considered contraindicated.', NULL, 'Coadministration of methadone with ketoconazole is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88307/', '[1] Martell BA, Arnsten JH, Krantz MJ, Gourevitch MN "Impact of methadone treatment on cardiac repolarization and conduction in opioid users." Am J Cardiol 95 (2005): 915-8[2] "Product Information. Nizoral (ketoconazole)." Janssen Pharmaceutica, Titusville, NJ. (1992):[3] De Bels D, Staroukine M, Devriendt J "Torsades de pointes due to methadone." Ann Intern Med 139 (2003): E156[4] Krantz MJ, Lewkowiez L, Hays H, et al "Torsade de pointes associated with very-high-dose methadone." Ann Intern Med 137 (2002): 501-4[5] Sala M, Anguera I, Cervantes M "Torsade de pointes due to methadone." Ann Intern Med 139 (2003): W64[6] Ehret GB, Desmeules JA, Broers B "Methadone-associated long QT syndrome: improving pharmacotherapy for dependence on illegal opioids and lessons learned for pharmacology." Expert Opin Drug Saf 6 (2007): 289-303[7] Gil M, Sala M, Anguera I, et al. "