INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88407, 'Ritonavir', 'Methadone', 'Moderate', 'Some protease inhibitors have been shown to decrease the plasma concentrations of methadone.', NULL, 'Caution is advised if protease inhibitors are prescribed to patients treated with methadone.', NULL, 'Caution is advised if protease inhibitors are prescribed to patients treated with methadone. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of protease inhibitor therapy in patients who are stabilized on their methadone regimen. Patients should be advised to notify their caregiver if they experience possible symptoms of methadone withdrawal such as restlessness, insomnia, sweating, lacrimation, and rhinorrhea.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88508/', '[1] Beauverie P, Taburet AM, Dessalles MC, Furlan V, Touzeau D "Therapeutic drug monitoring of methadone in HIV-infected patients receiving protease inhibitors." AIDS 12 (1998): 2510-1[2] Geletko SM, Erickson AD "Decreased methadone effect after ritonavir initiation." Pharmacotherapy 20 (2000): 93-4[3] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999): 403-12[4] Benmebarek M, Devaud C, Gex-Fabry M, et al. "Effects of grapefruit juice on the pharmacokinetics of the enantiomers of methadone." Clin Pharmacol Ther 76 (2004): 55-63[5] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the