INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88441, 'St. John''s Wort', 'Methadone', 'Moderate', 'The concomitant administration of St. John''s wort may reduce methadone plasma concentrations and cause withdrawal symptoms. The proposed mechanism is increased clearance of methadone due to induction of CYP450 3A4 metabolism and p-glycoprotein-mediated renal transport by the hypericin and hyperforin components in St. John''s wort.', NULL, 'Until more information is available, it may be advisable to monitor patients for altered efficacy of methadone and signs of withdrawal when St.', NULL, 'Until more information is available, it may be advisable to monitor patients for altered efficacy of methadone and signs of withdrawal when St. John''s wort is added to a regimen. Discontinuation of St. John''s wort may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88542/', '[1] Eich-Hochli D, Oppliger R, Golay KP, Baumann P, Eap CB "Methadone maintenance treatment and St. John''s Wort - a case report." Pharmacopsychiatry 36 (2003): 35-7[2] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999): 403-12[3] Benmebarek M, Devaud C, Gex-Fabry M, et al. "Effects of grapefruit juice on the pharmacokinetics of the enantiomers of methadone." Clin Pharmacol Ther 76 (2004): 55-63[4] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the N-demethylation of methadone in human liver microsomes." Chem Res Toxicol 9 (1996): 365-73[5] Oda Y, Kharasch ED "Metabolism of methadone and levo-alpha-acetylmethadol (LAAM) by human intestinal cytochrome P450 3A4 (CYP3A4): potential contribution of intestinal metabolism to presystemic clearance and bioactivation." J Pharmacol Exp Ther 298 (2001): 1021-32[6] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88442, 'Stiripentol', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6. The possibility of prolonged and/or increased pharmacologic effects of methadone, such as central nervous system and respiratory depression, should be considered. In addition, high dosages (particularly above 200 mg/day) and serum levels of methadone have been associated with QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen. Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88543/', '[1] Moody DE, Alburges ME, Parker RJ, Collings JM, Strong JM "The involvement of cytochrome P450 3A4 in the N-demethylation of L-alpha-acetylmethadol (LAAM), norLAAM, and methadone." Drug Metab Dispos 25 (1997): 1347-53[2] Krantz MJ, Lewkowiez L, Hays H, et al "Torsade de pointes associated with very-high-dose methadone." Ann Intern Med 137 (2002): 501-4[3] Ehret GB, Desmeules JA, Broers B "Methadone-associated long QT syndrome: improving pharmacotherapy for dependence on illegal opioids and lessons learned for pharmacology." Expert Opin Drug Saf 6 (2007): 289-303[4] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the N-demethylation of methadone in human liver microsomes." Chem Res Toxicol 9 (1996): 365-73[5] Oda Y, Kharasch ED "Metabolism of methadone and levo-alpha-acetylmethadol (LAAM) by human intestinal cytochrome P450 3A4 (CYP3A4): potential contribution of intestinal metabolism to presystemic clearance and bioactivation." J Pharmacol Exp Ther 298 (2001): 1021-32[6] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988): 623-9[7] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[8] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999): 403-12[9] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999): 403-12[10] Benmebarek M, Devaud C, Gex-Fabry M, et al. "Effects of grapefruit juice on the pharmacokinetics of the enantiomers of methadone." Clin Pharmacol Ther 76 (2004): 55-63[11] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the N-demethylation of methadone in human liver microsomes." Chem Res Toxicol 9 (1996): 365-73[12] Oda Y, Kharasch ED "Metabolism of methadone and levo-alpha-acetylmethadol (LAAM) by human intestinal cytochrome P450 3A4 (CYP3A4): potential contribution of intestinal metabolism to presystemic clearance and bioactivation." J Pharmacol Exp Ther 298 (2001): 1021-32', 1767365205);
