INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88455, 'Telaprevir', 'Methadone', 'Moderate', 'Coadministration with telaprevir may decrease the plasma concentrations of methadone. The mechanism of interaction has not been described.', NULL, 'Caution is advised if telaprevir is prescribed to patients treated with methadone.', NULL, 'Caution is advised if telaprevir is prescribed to patients treated with methadone. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of telaprevir in patients who are stabilized on their methadone regimen. Patients should be advised to notify their caregiver if they experience possible symptoms of methadone withdrawal such as restlessness, insomnia, sweating, lacrimation, and rhinorrhea.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88556/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999): 403-12[3] Benmebarek M, Devaud C, Gex-Fabry M, et al. "Effects of grapefruit juice on the pharmacokinetics of the enantiomers of methadone." Clin Pharmacol Ther 76 (2004): 55-63[4] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the N-demethylation of methadone in human liver microsomes." Chem Res Toxicol 9 (1996): 365-73[5] Oda Y, Kharasch ED "Metabolism of methadone and levo-alpha-acetylmethadol (LAAM) by human intestinal cytochrome P450 3A4 (CYP3A4): potential contribution of intestinal metabolism to presystemic clearance and bioactivation." J Pharmacol Exp Ther 298 (2001): 1021-32[6] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365205);
