INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376586, 'Cabergoline', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 1767317475, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376587, 'Cabergoline', 'Solriamfetol', 'Moderate', 'Coadministration of solriamfetol with other drugs that increase dopamine levels or that bind directly to dopamine receptors may increase the risk of dopaminergic-related adverse effects. The mechanism of the interaction has not been reported, but it may involve synergistic or additive central dopaminergic effects.', 'DDInter', 1767317475, 'Caution is advised if solriamfetol is administered concomitantly with other dopaminergic drugs. Dosage adjustments and close patient monitoring should be considered whenever solriamfetol is used with other dopaminergic drugs.', 'Synergism', 'Caution is advised if solriamfetol is administered concomitantly with other dopaminergic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
