INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376648, 'Cobimetinib', 'Cabozantinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of cobimetinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317475, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities and rhabdomyolysis, and the cobimetinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376649, 'Colchicine', 'Cabozantinib', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 1767317475, 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
