INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376651, 'Crizotinib', 'Cabozantinib', 'Major', 'Crizotinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting crizotinib therapy and periodically during treatment.', 'Synergism', 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376652, 'Cyclosporine', 'Cabozantinib', 'Moderate', 'Coadministration of cabozantinib with cyclosporine may increase exposure and toxicity of cabozantinib and/or cyclosporine. Both interactions are based on in vitro data. The proposed mechanisms include inhibition of P-glycoprotein-mediated drug efflux by cabozantinib (a P-gp inhibitor) which may potentially decrease the clearance and increase plasma concentrations of cyclosporine (a P-gp substrate). Additionally, cyclosporine, a multidrug resistance-associated protein inhibitor (MRP2 inhibitor) may increase cabozantinib (an MRP2 substrate) plasma concentrations due to inhibition of cabozantinib metabolism.', 'DDInter', 1767317475, 'Caution is advised if cabozantinib is used concomitantly with cyclosporine. Monitor for side effects of cabozantinib, such as diarrhea, hypertension, palmar-plantar erythrodysesthesia, hemorrhage, thrombotic events, or gastrointestinal perforation. For patients stabilized on a cyclosporine regimen, monitor blood levels and renal function. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever cabozantinib or cyclosporine are added to or withdrawn from combined therapy.', 'Absorption', 'Caution is advised if cabozantinib is used concomitantly with cyclosporine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376653, 'Dabrafenib', 'Cabozantinib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
