INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376694, 'Emtricitabine', 'Cabozantinib', 'Moderate', 'Coadministration of cabozantinib with multidrug resistance-associated proteins (MRP2) inhibitors may increase exposure and toxicity of cabozantinib. In vitro studies have shown MRP2 inhibitors may increase the plasma concentrations of cabozantinib, a MRP2 substrate.', 'DDInter', 1767317475, 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor. Monitor for cabozantinib toxicity, such as gastrointestinal perforations and fistulas, hemorrhage, thrombotic events, hypertension, diarrhea, and palmar-plantar erythrodysesthesia. Dose reductions, interruption or discontinuation of cabozantinib may be necessary if toxicity occurs.', 'Others', 'Caution is advised if cabozantinib is used concomitantly with a MRP2 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
