INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376749, 'Imatinib', 'Cabozantinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as nausea, vomiting, diarrhea, anorexia, weight loss, stomatitis, gastrointestinal perforations and fistulas, palmar-plantar erythrodysesthesia syndrome, hemorrhage, wound complications, hypertension, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
