INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376794, 'Cabozantinib', 'Mefenamic acid', 'Major', 'Coadministration of cabozantinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with cabozantinib is associated with severe, sometimes fatal hemorrhage including hemoptysis and gastrointestinal hemorrhage.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with cabozantinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with cabozantinib.', NULL, NULL, 'Major', 'pharmacodynamic');
