INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376803, 'Cabozantinib', 'Metronidazole', 'Minor', 'Limited data suggest that metronidazole may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with metronidazole.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376804, 'Cabozantinib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as nausea, vomiting, diarrhea, anorexia, weight loss, stomatitis, gastrointestinal perforations and fistulas, palmar-plantar erythrodysesthesia syndrome, hemorrhage, wound complications, hypertension, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
