INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376852, 'Cabozantinib', 'Pentosan polysulfate', 'Moderate', 'Theoretically, pentosan polysulfate sodium may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 1767317475, 'Caution is advised if pentosan polysulfate sodium is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if pentosan polysulfate sodium is used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376853, 'Cabozantinib', 'Perflutren', 'Major', 'Perflutren lipid microsphere suspension (Definity) has been reported to cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval. Please refer to the prescribing information for these drugs for more information. No data are available regarding the effect of perflutren protein-type A microsphere suspension (Optison) on the QT interval of the electrocardiogram.', 'Synergism', 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
