INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376925, 'Cabozantinib', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible. If chronic coadministration is required, the daily dosage of cabozantinib should be increased in accordance with the prescribing information for the specific product. For the treatment of progressive, metastatic medullary thyroid cancer (capsule formulation), the recommendation is to increase cabozantinib dosage by 40 mg (e.g., from 140 mg to 180 mg daily or from 100 mg to 140 mg daily) as tolerated, not to exceed 180 mg/day.', 'Metabolism', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1376926, 'Cabozantinib', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as nausea, vomiting, diarrhea, anorexia, weight loss, stomatitis, gastrointestinal perforations and fistulas, palmar-plantar erythrodysesthesia syndrome, hemorrhage, wound complications, hypertension, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
