INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88667, 'Disopyramide', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88768/', '[1] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88668, 'Disopyramide', 'Lefamulin', 'Major', 'When administered orally, lefamulin may significantly increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Increased exposure to sensitive CYP450 3A4 substrates that can prolong the QT interval may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death. Because lefamulin itself may cause prolongation of the QT interval, the potential for a pharmacodynamic interaction with other agents that can prolong the QT interval should also be considered.', NULL, 'Concomitant use of oral (but not intravenous) lefamulin with sensitive CYP450 3A4 substrates that can prolong the QT interval is considered contraindicated.', NULL, 'Concomitant use of oral (but not intravenous) lefamulin with sensitive CYP450 3A4 substrates that can prolong the QT interval is considered contraindicated. However, because lefamulin itself can also cause QT prolongation regardless of the route of administration, concomitant use with other drugs that can prolong the QT interval should generally be avoided even when lefamulin is administered intravenously.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88769/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[3] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[4] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767363022);
