INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (88861, 'Disopyramide', 'Tofacitinib', 'Moderate', 'Tofacitinib has been shown to decrease heart rate and prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta-blockers, digoxin, alpha-2 adrenoceptor agonists) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible. Caution is advised if concomitant use is necessary, particularly in patients with known conduction problems or severe cardiac disease. An ECG should be considered in these patients before initiating concomitant therapy and after titration to steady-state. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeats.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88962/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363022);
