INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377194, 'Calaspargase pegol', 'Prednisolone', 'Moderate', 'INTERVAL: The administration of asparaginase with or immediately preceding prednisone may increase the risk of hyperglycaemia. Concomitant use of asparaginase and corticosteroids may increase the risk of thrombosis or hemorrhage. The proposed mechanism may be related to asparaginase-induced fluctuation of coagulation proteins.', 'DDInter', 1767317475, 'Close monitoring for clinical and laboratory evidence of hyperglycemia and/or altered coagulation is also recommended at baseline and periodically throughout concurrent treatment with corticosteroids and asparaginase-derived products. Patients should be advised to promptly report any signs and symptoms of hyperglycemia, bleeding, blood clots or bone pain.', 'Synergism', 'Close monitoring for clinical and laboratory evidence of hyperglycemia and/or altered coagulation is also recommended at baseline and periodically throughout concurrent treatment with corticosteroids and asparaginase-derived products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
