INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377363, 'Cimetidine', 'Salmon calcitonin', 'Minor', 'In patients who had chronic renal failure and were receiving regular dialysis, administration of 400 mg of cimetidine decreased the elevated calcitonin levels associated with these conditions. The mechanism of this interaction is unclear, and the clinical significance is uncertain.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377364, 'Etelcalcetide', 'Salmon calcitonin', 'Major', 'Concomitant use of etelcalcetide with other medicines known to lower serum calcium may increase the risk of hypocalcemia. The proposed mechanism is additive reduction in serum calcium levels. Significant decreases in serum calcium may cause paraesthesias, myalgias, muscle spasms, seizures, QT interval prolongation, and ventricular arrhythmia. Etelcalcetide given alone may lead to severe hypocalcemia requiring concomitant treatment with therapies to increase serum calcium levels.', 'DDInter', 1767317475, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics. It is recommended that the patient''s corrected serum calcium level should be measured within one week of initiation or dose adjustment of etelcalcetide and monitored approximately every 4 weeks. In addition, the manufacturer product labeling should be consulted for management guidelines in the event of a reduction in corrected serum calcium levels below the lower limit of the normal range and/or symptoms of hypocalcemia. Patients should also be advised to contact their health care professional if they experience symptoms of hypocalcemia, such as numbness or tingling in the fingers, toes, or around the mouth, muscle spasms, twitches or cramps, seizures, confusion, and irregular heartbeats.', 'Synergism', 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377365, 'Salmon calcitonin', 'Lithium carbonate', 'Moderate', 'Calcitonin may decrease serum lithium levels by up to 30%. The mechanism may be related to an increase in lithium elimination or a decrease in lithium absorption caused by calcitonin.', 'DDInter', 1767317475, 'Lithium levels should be measured frequently and dosage adjustments made accordingly if these drugs are given concurrently. Patients should be advised to notify their physician if they experience drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, thirst, blurry vision, or increased urination.', 'Absorption, Excretion', 'Lithium levels should be measured frequently and dosage adjustments made accordingly if these drugs are given concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377366, 'Aluminum hydroxide', 'Calcitriol', 'Moderate', 'In patients with advanced renal impairment or on dialysis, chronic use of aluminum-containing preparations during treatment with vitamin D analogs may result in increased blood levels of aluminum and aluminum toxicity. Patients with renal failure are at risk for toxicity due to impaired clearance of aluminum, and the risk may be further increased by the administration of vitamin D analogs due to increased intestinal absorption of aluminum. Toxicity may manifest as osteodystrophy, osteomalacia, myopathy, anemia, and encephalopathy.', 'DDInter', 1767317475, 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', 'Others', 'Concomitant use of vitamin D analogs and aluminum-containing preparations should be followed with monitoring for signs and symptoms of aluminum toxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1377367, 'Calcitriol', 'Bendroflumethiazide', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients. Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium. Patients who are particularly susceptible include those with hyperparathyroidism, those being treated for osteoporosis, and those receiving high dosages of vitamin D for hypoparathyroidism. Metabolic alkalosis and the milk-alkali syndrome have been reported during prolonged therapy with thiazide diuretics and calcium.', 'DDInter', 1767317475, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider. Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D. Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Synergism', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', NULL, NULL, 'Moderate', 'pharmacodynamic');
