INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89224, 'Valproic acid', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects including anticonvulsants. Animal data suggest a possible pharmacokinetic interaction between sodium oxybate and certain anticonvulsants like valproate, phenytoin, ethosuximide, and trimethadione that can inhibit the enzyme GHB dehydrogenase, which converts GHB to succinic semialdehyde.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. For patients already stabilized on sodium oxybate who are prescribed divalproex sodium, the manufacturer recommends that the nightly dosage of sodium oxybate be reduced initially by at least 20%. Conversely, for patients already taking divalproex sodium, it is recommended that prescribers use a lower starting dosage of sodium oxybate upon initiation of treatment. Further dosage adjustments should be made according to patient response and tolerance. No specific dosage recommendations are available for use with other anticonvulsants.', 'Metabolism, Synergism', 'Isoflurane, Nitrous oxide, Desflurane, Enflurane, Halothane, Etomidate, Dalfampridine, Inotersen, Riluzole, Amifampridine', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89325/', '[1] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[2] "Product Information. Xywav (calcium/magnesium/potass/sodium oxybates)." Jazz Pharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89225, 'Valproic acid', 'Phenylbutyric acid', 'Major', 'Valproic acid can increase plasma ammonia levels and may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Hyperammonemic encephalopathy, sometimes fatal, has been reported following initiation of valproate therapy in patients with urea cycle disorders, particularly ornithine transcarbamylase deficiency.', NULL, 'Valproic acid should generally not be given to patients receiving phenylbutyrate therapy, as the use of valproic acid is considered contraindicated in patients with urea cycle disorders.', NULL, 'Valproic acid should generally not be given to patients receiving phenylbutyrate therapy, as the use of valproic acid is considered contraindicated in patients with urea cycle disorders.', 'Synergism', 'Brivaracetam, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Lipoic acid, Triheptanoin, Teduglutide, Fosdenopterin, Triethylenetetramine, Miglustat, Sapropterin, Zinc acetate, Nitisinone, Eliglustat, Laronidase, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89327/', '[1] "Product Information. Depakote (divalproex sodium)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Depakene (valproic acid)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.[4] "Product Information. Buphenyl (sodium phenylbutyrate)." Horizon Pharma USA Inc, Northbrook, IL.[5] "Product Information. Fycompa (peram