INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89660, 'Dofetilide', 'Mexiletine', 'Major', 'Dofetilide should not be used with Class I or other Class III antiarrhythmic agents due to the potential for additive effects on myocardial refractoriness. Many of these agents, including dofetilide, can also cause prolongation of the QT interval, thus concomitant use may increase the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', NULL, 'Class I (e.', NULL, 'Class I (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, ibutilide, sotalol) antiarrhythmic agents should be withheld for at least 3 half-lives before administering dofetilide. In the case of amiodarone with its unpredictable pharmacokinetics, dofetilide should not be initiated until serum amiodarone levels are below 0.3 mcg/mL or amiodarone has been withdrawn for at least three months.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89762/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89661, 'Dofetilide', 'Miconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may increase the plasma concentrations of dofetilide, which is partially metabolized by the isoenzyme. The use of dofetilide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. The interaction has not been studied with clotrimazole troches or miconazole buccal tablets. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 such as dofetilide cannot be ruled out.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of dofetilide, caution is advised during coadministration with clotrimazole or miconazole mucous membrane preparations.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of dofetilide, caution is advised during coadministration with clotrimazole or miconazole mucous membrane preparations. Patients should be cautioned about the potential for interaction and advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89763/', '[1] Cerner Multum, Inc. "UK Summ